#!/bin/bash
# CommandCenter Cleanup Script
# Auto-generated by Claude Code - Session 20

echo "ðŸ§¹ Cleaning CommandCenter project..."

# Remove Python cache
echo "  â†’ Removing Python cache..."
find backend -type d -name "__pycache__" -exec rm -rf {} + 2>/dev/null || true
find backend -type f -name "*.pyc" -delete 2>/dev/null || true
find backend -type f -name "*.pyo" -delete 2>/dev/null || true

# Remove Node.js cache (if exists)
if [ -d "frontend/node_modules" ]; then
    echo "  â†’ Cleaning Node.js cache..."
    rm -rf frontend/.cache 2>/dev/null || true
fi

# Remove common temporary files
echo "  â†’ Removing temporary files..."
find . -type f \( -name "*.tmp" -o -name "*.log" -o -name "*.swp" -o -name ".DS_Store" \) -delete 2>/dev/null || true

# Remove empty directories in .agent-coordination (except required structure)
echo "  â†’ Cleaning coordination directory..."
find .agent-coordination -type d -empty -delete 2>/dev/null || true

# Update memory.md timestamp
if [ -f ".claude/memory.md" ]; then
    echo "  â†’ Updating memory.md timestamp..."
    sed -i.bak "s/\*\*Last Updated\*\*:.*/\*\*Last Updated\*\*: $(date +%Y-%m-%d)/" .claude/memory.md
    rm -f .claude/memory.md.bak
fi

echo "âœ… Cleanup complete!"
